    map<pair<pair<ll,ll>,ll>,ll> mp;
    ll rec(ll now,ll last,ll jump,ll want){

        if(jump==32){
            return 0;
        }

        if(mp.count({{now,jump},last})){
            return mp[{{now,jump},last}];
        }

        ll ans=(now==want);

        if(last==0){
            ll to=(now+(1ll<<jump));
            ans=(ans+rec(to,1,jump+1,want));
        }
        else{
            ll to=(now+(1ll<<jump));
            ans=(ans+rec(to,1,jump+1,want));
            ans=(ans+rec(now-1,0,jump,want));
        }
        return mp[{{now,jump},last}]=ans;
    }
