class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def wtr(p, j):

            if p > k + 1:
                return 0

            if p == k + 1:
                return 1 + wtr(k + 2 ** j, j + 1)

            if p == k:
                return 1 + wtr(p - 1 + 2 ** j, j + 1) + wtr(p + 2 ** j, j + 1)

            if p < k:
                return wtr(p - 1 + 2 ** j, j + 1) + wtr(p + 2 ** j, j + 1)

        return wtr(1, 0)
