class Solution:
    def waysToReachStair(self, k: int) -> int:

        @functools.lru_cache(maxsize=None)
        def f(c, step, dec):
            if c > k + 10:
                return 0
            ret = 1 if c == k else 0
            if c + 2 ** step <= k + 10:
                ret += f(c + 2 ** step, step + 1, 1)
            if dec == 1:
                ret += f(c - 1, step, 0)
            return ret

        return f(1, 0, 1)
