class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i, j, canstep):

            if i < 0 or j < 0:
                return 0
            #print(i, j, canstep)
            if i == 1 and j == 0 and canstep:
                return 1

            return (dp(i + 1, j, True) if not canstep else dp(i - 2**(j-1), j - 1, False) + dp(i - 2**(j-1), j - 1, True))
        #print('awa')
        #return dp(1, 1, False)

        return sum([dp(k, j, False) + dp(k, j, True) for j in range(32)])
