class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(stair, jump, prev_dec):
            res = bool(stair == k)
            op1 = stair - 1
            op2 = stair + 2 ** jump
            if not prev_dec and op1 >= 0:
                res += dp(op1, jump, True)
            if jump < 1 or op2 - 1 <= k:
                res += dp(op2, jump + 1, False)
            return res
        return dp(1, 0, False)
