class Solution {
	public int waysToReachStair(int k) {
		long ans = 0;
		// 1 0
		// 2 1
		// 2 1 0
		// 4 3 2
		// 4 3 2 1
		// 8 7 6 5

		long[][] C = new long[60 + 1][60 + 1];
		for (int i = 0; i <= 60; i++) {
			C[i][0] = 1;
			for (int j = 1; j <= i; j++) {
				C[i][j] = C[i - 1][j - 1] + C[i - 1][j];
			}
		}
		for(int d = 0;d <= 29;d++){
			int base = 1<<d+1;
			if(k <= base && k >= base - (d+1)){
				ans += C[d+1][base-k];
			}
			if(k <= base && k >= base - (d+2)){
				ans += C[d+2][base-k] - C[d+1][base-k];
			}
		}
		if(k <= 1)ans++;
		return (int)ans;
	}
}
