class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dp(stair = 1, jump = 0, wentBack = False):

            if stair > k + 2:
                return 0

            ways = int(stair == k)


            ways += dp(stair + (1 << jump), jump + 1, False)

            if stair > 0 and not wentBack:
                ways += dp(stair - 1, jump, True)

            return ways

        return dp()
