class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0: return 2

        @cache
        def go(curr, jump, canDown):
            if curr > 10 * k: return 0

            res = 0

            if curr == k: res += 1

            if curr != 0 and canDown:
                res += go(curr - 1, jump, False)

            res += go(curr + pow(2, jump), jump + 1, True)

            return res

        return go(1, 0, True)
