#include <bits/stdc++.h>
using namespace std;
#define rep(i, a, b) for(int i = a; i < (b); ++i)
#define all(x) begin(x), end(x)
#define sz(x) (int)(x).size()
#define PB push_back
#define FS first
#define SD second
#define cmx(x,y) x = max(x,y)
#define cmn(x,y) x = min(x,y)
#define ary(k) array<int,k>
typedef pair<int, int> pii;
typedef vector<int> vi;
typedef long long ll;
class Solution {
public:
    int waysToReachStair(int k) {
        const int MX = 32;
        vector<vector<int64_t>> b(MX,vector<int64_t>(MX));
        b[0][0] = 1;
        for(int i = 1; i < MX; i++){
            for(int j = 0; j <= i; j++){
                b[i][j] = b[i-1][j];
                if(j) b[i][j] += b[i-1][j-1];
            }
        }
        ll sum = 1,ans = 0;
        for(int mx = 0; mx <= 30; mx++){
            ll d = k-sum;   
            if(d <= 0){
                d = -d;
                // place -1s around
                ll gaps = mx+1;         
                // ans = c(gaps,d)
                if(gaps >= d){
                    //cerr << gaps << " " << d << "\n";
                    ans += b[gaps][d];
                }
            }
            sum += 1<<mx;
        } 
        return ans;
    }
};
