typedef long long LL;



class Solution {
public:
    int waysToReachStair(int k) {
        LL C[32][32];
        memset(C,0,sizeof(C));
        C[0][0]=1;
        for (int i = 1; i < 32; ++i) {
            C[i][0]=C[i][i]=1;
            for (int j = 1; j < i; ++j) {
                C[i][j] = C[i-1][j-1] + C[i-1][j];
            }
        }
        int res = 0;
        LL g = 1;
        LL w = 1;
        for (int i = 0; i < 30; ++i) {
            LL p = w;
            if (k<=p) {
                LL d = p-k;
                if (d<=g) {
                    res += C[g][d];
                }
            }

            ++g;
            w+=1ll<<i;
        }
        return res;
    }
};
