class Solution {
public:
    #define ll long long
    int waysToReachStair(int k) {
        ll ans = 0, val = 1, sum = 0;
        for(ll i = 0; i <= 30; i++){
            val = 1;
            for(ll j = 0; j <= i+1; j++){
                if(j){
                    val *= (i-j+2);
                    val /= (j);
                }

                if(sum-j == k-1) ans += val;
            }
            sum += 1LL<<i;
        }
        return ans;
    }
};
