class Solution:
    def waysToReachStair(self, tar: int) -> int:
        down_count = 1
        hop_dist = 1
        cur = 1
        ans = 0
        if cur - down_count <= tar:
            ans += math.comb(down_count, tar - cur + down_count)

        while cur - down_count <= tar:
            cur += hop_dist
            hop_dist <<= 1
            down_count += 1


            if cur - down_count <= tar:
                ans += math.comb(down_count, tar - cur + down_count)

            # print(cur, down_count)


        return ans
