class Solution {
public:
    int temp;
    unordered_map<long long ,int>m[2][38];
    int make(int la,int j,long long curr)
    {
        if(curr-8>temp)
            return 0;
        if(m[la][j].find(curr)==m[la][j].end())
        {
            int ans=0;
            if(curr==temp)
                ans++;
            if(curr!=0&&la==0)
            {
                ans+=make(1,j,curr-1);
            }
            ans+=make(0,j+1,curr+(1<<j));
            m[la][j][curr]=ans;
        }
        return m[la][j][curr];

    }
    int waysToReachStair(int k) {
        for(int i=0;i<2;i++)
        {
            for(int j=0;j<38;j++){
                m[i][j].clear();
            }
        }
        temp=k;
        return make(0,0,1);

    }
};
