ll mod=MOD1;
ll po(ll x,ll y,ll _prime=mod) {if(y<0)return 0;y%=(_prime-1);ll res=1;while(y>0){if(y&1)res=(res*x)%_prime;x=(x*x)%_prime;y>>=1;}return (res%_prime);}
ll gcd(ll a, ll b){if(a<b) swap(a,b);if(b==0) return a;return gcd(a%b,b);}
ll choose(int n,int r){
    if(n<r || r<0)return 0;
    ll f1=1,f2=1,f3=1;
    rep(i,1,n+1)f1=f1*i%mod;
    rep(i,1,n-r+1)f2=f2*i%mod;
    rep(i,1,r+1)f3=f3*i%mod;
    return f1*po(f2,mod-2)%mod*po(f3,mod-2)%mod;
}
class Solution {
public:
    int waysToReachStair(int k) {
        int s=1;int ans=0;
        for(int j=0;;j++){
            int l=s-j-1;
            int r=s;
            if(k>=l && k<=r){
                int m=s-k;
                ans+=choose(j+1,m);
            }
            if(l>k)break;
            s+=1<<j;
        }return ans;
    }
};
