class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(i, curJump, isDown):
            if i > k + 1:
                return 0
            if i == k + 1:
                if isDown:
                    return 0
                else:
                    return dfs(i - 1, curJump, True)
            if i == k:
                res = 1
            else:
                res = 0

            if not isDown:
                res += dfs(i - 1, curJump, True)

            res += dfs(i + 2 ** curJump , curJump + 1, False)
            return res

        return dfs(1, 0, False)
