class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def dfs(i, jump, is_cons):
            if jump >= 32: return 0
            ans = 0
            if i == k:
                ans += 1
            if i < 0:
                return 0
            if i > 0 and not is_cons:
                ans += dfs(i - 1, jump, True)
            ans += dfs(i + (1 << jump), jump + 1, False)
            return ans

        return dfs(1, 0, False)
