class Solution:
    def waysToReachStair(self, k: int) -> int:
        k -= 1
        ans = 0
        for i, tot in enumerate([(1 << jump) - 1 for jump in range(40)], 1):
            if tot - i <= k <= tot:
                # print(i, tot - k)
                ans += comb(i, tot - k)
        return ans
