class Solution:

    @cache
    def solve(self, step, jump, target, down = False, iter = 0):
        r = 0
        if iter == 32:
            return 0
        if step == target:
            r += 1
        if step > target or step < 0:
            return 0

        if not down:
            r += self.solve(step - 1, jump, target, True)
        r += self.solve(step + pow(2, jump), jump + 1, target)
        return r

    def waysToReachStair(self, k: int) -> int:
        return self.solve(1, 0, k) + self.solve(1, 0, k + 1)
