/* eslint-disable no-var, func-style */
const combo = createCombo(40)
function createCombo(n) {
    const r = []
    for (let i = 1; i <= n; i++) {
        r[i] = []
        for (let j = 0; j <= n; j++) {
            if (i === 1 || !j || i <= j) {
                r[i][j] = 1
                continue
            }
            r[i][j] = r[i - 1][j - 1] + r[i - 1][j]
        }
    }
    return r
}
/**
 * @param {number} k
 * @return {number}
 */
var waysToReachStair = function(k) {
    const log = 30
    let r = 0
    for (let jump = 0; jump < log; jump++) {
        const back = (1 << jump) - k
        if (back < 0 || back > jump + 1) continue
        // console.log([jump, back])
    // console.log([jump + 1, back])
        r += combo[jump + 1][back]
    }
    return r
};
