class Solution:
    @cache
    def dp(self, k, jump, just_down):
        ans = 0
        if not just_down:
            ans += self.dp(k+1, jump, True)
        if k - 2** jump >= -1:
            ans += self.dp(k - 2**jump, jump+1, False)
        if k == 1:
            ans += 1

        return ans

    def waysToReachStair(self, k: int) -> int:
        return self.dp(k, 0, False)
