using ll = __int128;
class Solution {
public:
    int waysToReachStair(int k) {



        ll x = k;
        ll ans = 0;

        for (long long i = 0; i < 31; ++i) {
            ll u = i;
            ll up = (1LL<<(i))-1;
            ll down = up+1-x;
            if (down > u+1) break;
            if (down < 0) continue;


            // dud ud u u u
            // d <= u

            // from u choose d
            // u! / d! (u-d)!

            ll s = 1;
            for (ll i = u+1; i > down; --i) s *= i;
            for (ll i = 1; i <= (u+1-down); ++i) s /= i;

            ans += s;

        }
        return ans;
    }
};
