class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(i, jump, st) :
            if i > k+1:
                return 0

            res = dfs(i+jump, jump*2, 0) + int(i == k)
            if not st and i != 0 :
                res += dfs(i-1, jump, 1)
            return res

        ans = dfs(1, 1, 0)
        return ans
