using ll = long long;
map<pair<ll,ll>,int>dp;
class Solution {
public:
    int solve(ll i,ll j,ll k){
        if (dp.count({i,j}))return dp[{i,j}];
        if (i - k > 1)return dp[{i,j}] = 0;
        dp[{i,j}] = (i - k == 1 or i - k == 0 ? 1 : 0) + (i == 0 ? 0 : solve(i - 1 + (1ll<<j),j + 1,k)) + solve(i + (1ll<<j),j + 1,k);
        return dp[{i,j}];
    }
    int waysToReachStair(ll k) {
        dp.clear();
        return solve(1,0,k);
    }
};
