map<int,int> mp[31][2];
int f(long long n,int jump,int k,bool can){
    if(n-k>2)return 0;
    if(mp[jump][can].count(n))return mp[jump][can][n];
    int res=(n==k);
    if(can && n!=0){
        res+=f(n-1,jump,k,0);
    }
    res+=f(n+((1LL<<jump)),jump+1,k,1);
    return mp[jump][can][n]=res;
}
class Solution {
public:
    int waysToReachStair(int k) {
        map<int,int> dp;
        for(int i=0;i<31;i++){
            mp[i][0]=dp;
            mp[i][1]=dp;
        }
        return f(1,0,k,1);
    }
};
