class Solution:
    def waysToReachStair(self, k: int) -> int:


        @lru_cache(None)
        def f(s, c1, jmp):
            # print(s, c1, jmp)
            if s >= k+2:
                return 0

            ret = 1 if (s == k) else 0

            if c1 and s != 0:
                ret += f(s-1, False, jmp)

            ret += f(s + (1<<jmp), True, jmp+1)


            return ret

        return f(1, True, 0)
