class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def can(stair, jump, wentdown):
            res = 0
            if not wentdown and stair > 0:
                res = can(stair - 1, jump, True)
            if stair > k:
                return res
            if stair == k:
                res += 1
            res += can(stair + 2**jump, jump + 1, False)
            return res
        return can(1, 0, False)
