class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dp(i,op1,jump):
            if i == k:
                res = 1
            else:
                res = 0
            if i > k+1:
                return 0
            if op1 or i==0:
                return res + dp(i+2**jump, False, jump+1)
            else:
                return res + dp(i-1, True, jump) + dp(i+2**jump, False, jump+1)
        return dp(1, False, 0)
