from functools import lru_cache

class Solution:
    def waysToReachStair(self, k: int) -> int:
        self.target = k
        return self.dp(1, 0, False)

    @lru_cache
    def dp(self, position, jump, used_back):
        if position > self.target + 1:
            return 0

        num_ways = 0
        if position == self.target:
            num_ways += 1

        if position > 0 and not used_back:
            # go back
            num_ways += self.dp(position - 1, jump, True)

        # jump
        num_ways += self.dp(position + 2 ** jump, jump + 1, False)

        return num_ways
