class Solution:
    def waysToReachStair(self, k: int) -> int:


        @cache
        def dp(i, jump, consec):
            res = 0
            if i == k:
                res = 1

            if i > k + 1:
                return 0

            if consec or i == 0:
                return res + dp(i + (1 << jump), jump + 1, False)

            return res + dp(i + (1 << jump), jump + 1, False) + dp(i - 1, jump, True) 

        return dp(1, 0, False)
