from functools import cache


@cache
def ways(k, jump=0, down=False):
    s = 0

    if k == 0:
        s += 1

    if k < -1:
        return 0

    s += ways(k - 2**jump, jump + 1)
    if not down:
        s += ways(k + 1, jump, True)

    return s


class Solution:
    def waysToReachStair(self, k: int) -> int:
        return ways(k - 1)
