class Solution {
    unordered_map<int, vector<vector<int>>> mp;
public:
    int waysToReachStair(int k) {
        return solve(1, 0, 0, k);
    }

    int solve(int i, int j, int t, int k) {
        if (mp.count(i) && mp[i][j][t] != -1) return mp[i][j][t];

        if (i > k+1) return 0;

        int ans = 0;
        if (i == k) ans = 1;
        ans += solve(i + (1<<j), j+1, 0, k);

        if (t == 0 && i > 0) ans += solve(i-1, j, 1, k); 

        mp[i] = vector(32, vector(2, -1));
        mp[i][j][t] = ans;

        return ans;
    }
};
