class Solution:
    def waysToReachStair(self, k: int) -> int:


        @cache
        def dp(i, used, jump):
            #if i == k:
            #    return 1

            if i > k+1:
                return 0

            #if i == 0:
            #    return dp(i + 2**jump, False, jump+1)

            tot = 0

            if i == k:
                tot += 1

            if not used and i != 0:

                tot += dp(i-1,True, jump)

            tot += dp(i + 2**jump, False, jump+1)

            return tot

        return dp(1, False, 0)
