const int mod = 998244353;
const int N = 40;

int power(int a, int b){
	int res = 1;
	while(b){
		if(b&1) res = (1ll*a*res)%mod;
		b>>=1;
		a = (1ll*a*a)%mod;
	}
	return res;
}

int fact[N];
int invFact[N];

int choose(int n, int r){
	if(r>n) return 0;
	int ans = (1ll*fact[n]*invFact[r])%mod;
	return (1ll*ans*invFact[n-r])%mod;
}

class Solution {
public:
    int waysToReachStair(int k) {
        fact[0] = 1;
        for(int i=1;i<N;i++){
            fact[i] = (1ll*i*fact[i-1])%mod;
        }
        invFact[N-1] = power(fact[N-1], mod-2);
        for(int i=N-2;i>=0;i--){
            invFact[i] = (1ll*invFact[i+1]*(i+1))%mod;
        }
        if(k>4){
            int x = 1;
            int y = 0;
            while(x<k){
                x+=(1<<y);
                y++;
            }
            y++;
            return choose(y, x-k);
        }
        if(k==0) return 2;
        if(k==1) return 4;
        if(k==2) return 4;
        if(k==3) return 3;
        return 2;
    }
};
