class Solution {
public:
    int nCk(int a, int b){
        if(a-b < b) return nCk(a, a-b);
        #define ll long long
        ll res = 1;
        vector<int> v(b+1);
        for(int i=0; i<b; i++){
            res *= (a-i);
            for(int j=1; j<=b; j++){
                if(!v[j] && res % j == 0){
                    res /= j;
                    v[j] = 1;
                }
            }
        }
        return res;

    }
    int waysToReachStair(int k) {
        int res=0;
        for(int j=0, dx=1; ; dx += (1 << (j++))){
            if(dx - (j+1) > k) break;
            if(dx < k) continue;
            int down = dx - k;
            // cout << j << ", " << down << endl;
            res += nCk(j+1, down);
        }
        return res;
    }
};
