class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i,prev_jumped_down, jump):
            if i >= k+2: return 0
            res = 0
            if i == k: res += 1
            if i == 0 or prev_jumped_down:
                return res + dp(i+2**jump, False, jump+1)
            return res + dp(i-1, True, jump) + dp(i+2**jump, False, jump+1)




        return dp(1, False, 0)
