class Solution {
public:
    map<vector<int>,int> dp;
    int f(int k, int i, int j, int x){
        if(i>k+1){
            return 0;
        }
        vector<int>v({k, i, j, x});
        if(dp.find(v)!=dp.end()){
            return dp[v];
        }
        int ans=0;
        if(i==k) ans++;
        ans+=f(k, i+(1<<j), j+1, 0);
        if(x==0){
            ans+=f(k, i-1, j, 1);
        }
        dp[v]=ans;
        return ans;
    }

    int waysToReachStair(int k) {
        dp.clear();
        return f(k, 1, 0, 0);
    }
};
