class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def backtrack(pos, jump, k):
            if k+1 < pos: return 0

            result = 0
            if pos == k: result += 1


            next_pos = pos + 2**jump

            k, next_pos = k - next_pos + 1, 1

            return result + backtrack(next_pos, jump+1, k) + backtrack(next_pos-1, jump+1, k)



        return backtrack(1, 0, k) + backtrack(1-1, 0, k)
