class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def recur(wentDown, jump, stair):
            res = 0
            if stair > k+1: return res
            if stair == k: res += 1
            if not wentDown: res += recur(True, jump, stair-1)
            res += recur(False, jump+1, stair+2**jump)
            return res

        return recur(False, 0, 1)
