class Solution:
    def waysToReachStair(self, k: int) -> int:
        @functools.cache
        def solve(x: int, c: int) -> int:
            ret = 0
            if x == k:
                # stop
                ret += 1
            if x == k + 1:
                # -1, stop
                ret += 1
            if x >= 1 and x - 1 + (1 << c) <= k + 1:
                # -1, 2^c, ...
                ret += solve(x - 1 + (1 << c), c + 1)
            if x + (1 << c) <= k + 1:
                # 2^c, ...
                ret += solve(x + (1 << c), c + 1)
            return ret

        return solve(1, 0)
