class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i, jump, was_down):
            count = int(i == k)
            if not was_down and i != 0:
                count += dp(i - 1, jump, True)
            increased_i = i + (1 << jump)
            if increased_i <= k + 1:
                count += dp(increased_i, jump + 1, False)
            return count

        return dp(1, 0, False)
