class Solution {
public:
    map<vector<long long int>,long long int> m;
    long long int func(long long int rem,long long int j,long long int can,long long int k)
    {        
        if(rem<-4)return 0;
        if(m.find({rem,j,can})!=m.end())return m[{rem,j,can}];
        long long int ans = (long long int)rem==0;
        if(can && rem!=(k+1))
        {
            ans+=func(rem+1,j,!can,k);
        }
        long long int t = 1ll<<j;
        ans +=func(rem-t,j+1,1,k);
        return m[{rem,j,can}]=ans;
    }
    int waysToReachStair(int k) {
        return func(k-1,0,1,k-1);
    }
};
