class Solution(object):
    def waysToReachStair(self, k):
        """
        :type k: int
        :rtype: int
        """

        def comb(a, b):
            if a - b < b:
                b = a - b
            if b == 0:
                return 1

            nume = a
            deno = b
            for i in range(1, b):
                deno *= i
                nume *= (a - i)

            return nume / deno




        if k == 0:
            return 2
        if k == 1:
            return 4

        op = 0
        cur = 1
        res = 0

        while cur < k:
            cur *= 2
            op += 1

        for _ in range(0, 30):
            # print cur, op
            if cur - (op + 1) <= k:
                res += comb((op + 1), cur - k)
            else:
                break

            cur *= 2
            op += 1

        return res
