class Solution {
public:
    map<array<int,3>, int> mp; 
    int solve(int ind, bool last, int jump, int &k) {
        int ans = 0;
        if(ind > k + 1) return 0;
        if(ind == k) ans = 1;
        if(mp.find({ind, last, jump}) != mp.end()) return mp[{ind, last, jump}];

        if(ind != 0 && last == false) ans += solve(ind-1, true, jump, k);
        long long num = ind + 0ll + (1ll<<jump);
        if(num < k+2) ans += solve(num, false, jump+1, k);
        return mp[{ind, last, jump}] = ans;
    }
    int waysToReachStair(int k) {
        mp.clear();
        return solve(1, false, 0, k);

    }
};
