class Solution {
public:
map<int,map<int,map<int,int>>>mp;
int dfs(int stair, int last, int jump, int &target)
{
    if (stair > target + 1)
    {
        return 0;
    }
    if(mp[stair][last].find(jump)!=mp[stair][last].end()){
        return mp[stair][last][jump];
    }
    int ans = dfs(stair + (1LL << jump), 0, jump + 1, target);
    if (stair > 0 && last != 1)
    {
        ans += dfs(stair - 1, 1, jump, target);
    }
    if(stair==target) ans +=1;
    return mp[stair][last][jump]= ans;
}

int waysToReachStair(int k)
{
    return dfs(1, 0, 0, k);
}
};
