using ll = long long;
class Solution {
public:
    int waysToReachStair(int k) {
        int ans = 0, up_count = 0, up = 0;
        while(true) {
            int down_count = up + 1 - k;

            if(down_count > up_count + 1) return ans;
            ans += nChooseK(up_count + 1, down_count);

            up += 1 << up_count++;
        }
        return ans;
    }

    ll nChooseK(ll n, ll k) {
        if(k < 0) return 0;
        if(k == 0) return 1;

        ll up = 1, ans = 1;
        for(ll down = n; down > n-k; --down) {
            ans *= down;
            while(up <= k && ans % up == 0)
                ans /= up, ++up;
        }
        return ans;
    }
};
