class Solution {
public:


    map<vector<int>,int> H;
    int Solve(int pos, bool prev, int j, int k) {        
        if (pos >= k+2) return 0;
        int ans = (pos==k);
        auto it = H.find({pos,prev,j});
        if (it != H.end()) return it->second;
        ans += Solve(pos+(1<<j),false,j+1,k);
        if (!prev && pos > 0) ans += Solve(pos-1,true,j,k);
        return H[{pos,prev,j}]=ans;
    }

    int waysToReachStair(int k) {
        return Solve(1,false,0,k);
    }
};
