class Solution:
    def waysToReachStair(self, k: int) -> int:
        jump = 0 
        pos = 1
        ret = 0
        while True:
            if pos >= k:
                diff = pos - k 
                able_to_jump = jump + 1 
                if diff > able_to_jump:
                    break 
                ret += math.comb(able_to_jump, diff)
            pos += math.pow(2, jump)
            pos = int(pos)
            jump += 1 
        return ret
