class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i,back,jump):
            if i == k:
                ret = 1
                if back:
                    ret += dp(i-1,False,jump)
                ret += dp(i + 2**jump, True, jump+1)
                return ret
            if i > k + 1:
                return 0

            ret = 0
            if back:
                ret += dp(i-1, False,jump)

            ret += dp(i + 2**jump, True, jump+1)

            return ret



        return dp(1,True,0)
