class Solution {
public:
    int waysToReachStair(int k) {
        constexpr int mod = 1'000'000'007;
        vector<vector<int>> combs(33, vector<int>(33));
        for (int i = 0; i < combs.size(); ++i) {
            combs[i][0] = combs[i][i] = 1;
            for (int j = 1; j < i; ++j) {
                combs[i][j] = (combs[i - 1][j] + combs[i - 1][j - 1]) % mod;
            }
        }
        int result = 0;
        for (int n = 0; n <= 30; ++n) {
            const int m = (1 << n) - k;
            if (m < 0 || m > n + 1) {
                continue;
            }
            result += combs[n + 1][m];
        }
        return result;
    }
};
