class Solution:
    def waysToReachStair(self, k: int) -> int:
        sol = 0
        for jumpup in range(k + 2):
            ma = (1 << jumpup)
            mi = ma - jumpup - 1
            if mi <= k <= ma:
                nn = ma - mi
                kk = k - mi
                sol += math.comb(nn, kk)
            elif k < mi:
                break

        return sol
